import React, { useState, useEffect } from "react";
// import '..css/FeedSectionMain.css';
import ItemView from "./ItemView";
import '../css/Newsfeed.css';
import profileImage from '../img/profileImage.jpg';


function FeedSectionMain() {
  const [items, setItems] = useState([]);
  useEffect(() => {
      // fetch
    setItems([
      {
        _id: "5ec54bdf532d0bd486e405fe",
        userI: "5e992de9b13e618945705c10",
        itemName: "Toilet Paper",
        itemPrice: 15,
        itemDescription: "We are giving out at free food",
        itemCategory: "Education",
        itemMedium: "Online",
        itemLocation: "Dorchester",
        boughtBy: null,
        imageURL: "https://via.placeholder.com/200",
      },
      {
        _id: "5ec54bdf532d0b9486e405fe",
        userI: "5e992de9b13e618945705c10",
        itemName: "Clorox",
        itemPrice: 15,
        itemDescription: "We are giving out at free cleaning supplies",
        itemCategory: "Education",
        itemMedium: "Online",
        itemLocation: "Jamaica Plain",
        boughtBy: null,
        imageURL: "https://via.placeholder.com/200",
      },
      {
        _id: "5e992de9b13e61956212455831",
        userI: "5e992de9b13e618945705c10",
        itemName: "Flower Pots",
        itemPrice: 15,
        itemDescription: "We are giving out at free flower potst",
        itemCategory: "Education",
        itemMedium: "Online",
        itemLocation: "Dorchester",
        boughtBy: null,
        imageURL: "https://via.placeholder.com/200",
      },
      {
        _id: "5e992de9b13e61956212455832",
        userI: "5e992de9b13e618945705c10",
        itemName: "Coffee Table",
        itemPrice: 15,
        itemDescription: "Gold and Marble from West Elm",
        itemCategory: "Furniture",
        itemMedium: "Online",
        itemLocation: "Brockton",
        boughtBy: null,
        imageURL: "https://via.placeholder.com/200",
      },
      {
        _id: "5e982de9b13e61956212455832",
        userI: "5e992de9b13e618945705c10",
        itemName: "Water Bottles",
        itemPrice: 15,
        itemDescription: "We are giving out at free water bottles",
        itemCategory: "Education",
        itemMedium: "Online",
        itemLocation: "Dorchester",
        boughtBy: null,
        imageURL: "https://via.placeholder.com/200",
      },
    ]);
  },[]);

  return (
    <section className="board">
      {items.map((item, i) => {
        return <a href="/postedItem/:id"><ItemView className="cell" key={(i).toString()} item={item} imageURL={profileImage}/></a>;
      })}
    </section>
  );
}

export default FeedSectionMain;
